Ext.define('MyApp.view.SearchSiteConfigGridPanelViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.searchsiteconfiggridpanel',

    onApplyBtnClick: function(button, e, eOpts) {
        var grid = this.getView();
        var gridStore= this.getViewModel().getStore('searchGridStore');
        var selectedRows=gridStore.getData().items;
        MyApp.getApplication().fireEvent('selectedRowsResult',selectedRows);
    },

    onAddBtnClick: function(button, e, eOpts) {
        var gridStore= this.getViewModel().getStore('searchGridStore');
        var newRec = new MyApp.model.SearchGridModel(
        {'StartHour':'',
            'EndHour':'',
            'PyxMode':''

        });
        gridStore.insert(0,newRec);
    },

    onFlyBtnClick: function(button, e, eOpts) {
        var fly= this.getViewModel().getWin('flyWindow');
        fly.show();
    }

});
